mapper.select:
SELECT coffee_id, coffee_name, price, stock
FROM COFFEE
WHERE coffee_id = #{coffeeId}

mapper.update:
UPDATE COFFEE
SET stock = stock - #{quantity}
WHERE coffee_id = #{coffeeId}

mapper.select:
SELECT *
FROM DETAIL
WHERE order_num IN (
SELECT order_num FROM ORDERS
WHERE email = #{email} AND post_num = #{postNum}
)

mapper.delete:
DELETE FROM DETAIL
WHERE order_num IN (
SELECT order_num FROM ORDERS
WHERE email = #{email} AND post_num = #{postNum}
)

DELETE FROM DETAIL
WHERE order_num IN (
SELECT order_num FROM ORDERS
WHERE is_delivered = #{isDelivered}
)
